Fancybox.bind("[data-fancybox]",{hideScrollbar:!0,closeButton:!1}),$(".file__message").on("change",function(){var t=Array.from($(this)[0].files);let o=$(".request__photo-wrapper");o.empty(),t.forEach(t=>{o.append(`
         <button class="request__photo photo" type="button">
                <span class="photo__name">${t.name}</span>
                <i class="fa-solid fa-xmark"></i>
            </button>`)})}),$(document).on("click",".photo",function(){$(this).find(".photo__name").text().trim(),$(".file__message")[0].files,$(".photo").index(this);$(this).slideUp(300,function(){$(this).remove()})});